        }


        .menu {
            position: absolute; //cambio

            @if $direccion==r {
                right: calc(100% - 60px);
            }

            @else {
                left: 100%;
                float: left;
            }

            border: $bcolor solid $borderTk;
            border-radius: 3%;
            box-shadow: 5px 5px 15px $bMnu;
            transform: translatey(150px);
            top: 0px;
            padding: 0;
            margin: 0;


            // left: 0;
            @if $ancho==a {
                width: auto;
            }

            @else {
                width: $ancho;
            }

            height: auto; //100
            visibility: hidden;
            overflow: visible;
            display: flex;
            align-items: center;
            justify-content: center;



            >div {
                // define un fondo de pantalla para todo el menu
                background: $bMnu;
                width: 100%; ///  vh
                height: auto; // vh
                display: flex;
                align-items: flex-start;
                justify-content: flex-start;
                transform: scale(0);
                transition: all $tiempoCorto ease;


                >div {
                    //CONTENEDOR DEL MENU
                    margin: 3px;
                    text-align: center;
                    max-width: 90vw;
                    max-height: 100vh;
                    opacity: 0;
                    transition: opacity $tiempoCorto ease;
                    width: 100%;

                    >ul {
                        margin: 0;
                        padding: 0;
                        width: 100%;
                    }

                    >ul>li {
                        //item

                        margin: $marginItem;
                        list-style: none;
                        color: setcolor($bcolor); //revisar
                        font-size: size($fontsize);
                        width: 100%;
                        // padding: $pyItem $pxitem;

                        >a {
                            //propiedades del link
                            color: inherit;
                            text-decoration: none;
                            transition: color $tiempoCorto ease;
                            text-align: center;
                            margin: 0 auto;

                        }
                    }
                }
            }
        }

        @include s {
            
            .menu {
                visibility: visible;
                transform: translatey(120px);
                min-width: 350px;
                >div {                transform: scale(1);

                    >div {                    opacity: 1;
                    }
                }
            }
            .toggler{z-index: 5;}
            .toggler:checked~.menu {
                visibility: hidden;
    
                >div {
                    transform: scale(0);
                    transition-duration: $tiempoMedio;
    
                    >div {
                        opacity: 0;
                        transition: opacity $tiempoCorto ease 0.4s;
                    }
                }
            }
        }
    }

}

